// Get the request method
const method = $json.method || 'POST';
const headers = $json.headers || {};
const body = $json.body || {};

// Handle OPTIONS preflight request
if (method === 'OPTIONS') {
  return {
    statusCode: 200,
    body: { message: 'OK' },
    preflight: true
  };
}

// Check API key authentication (only for non-OPTIONS requests)
const apiKey = headers['x-api-key'];
const validApiKey = 'local-dev-api-key-12345';

if (!apiKey) {
  throw new Error('Missing API key. Please provide X-API-Key header');
}

if (apiKey !== validApiKey) {
  throw new Error('Invalid API key');
}

// Validate required fields
if (!body.query) {
  throw new Error('Missing required field: query');
}
if (!body.sessionId) {
  throw new Error('Missing required field: sessionId');
}

// Sanitize query (max 200 chars)
body.query = body.query.trim().substring(0, 200);

// Add timestamp and auth info
body.processedAt = new Date().toISOString();
body.authenticated = true;

// Return the validated data
return body;
